  void updateTijd(index) async {
    WereldTijd instantie = index;
    await instantie.getTime();
    Navigator.pop(context, {
      'locatie': instantie.locatie, 'tijd': instantie.tijd, 'isDagLicht': instantie.isDagLicht});
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: Colors.blue[900],
        title: const Text('Kies een locatie'),
        centerTitle: true,
        elevation: 0,
      ),
      body: ListView.builder(
        itemCount: locaties.length,
        itemBuilder: (context, index) {
          return Padding(
            padding: const EdgeInsets.symmetric(vertical: 1, horizontal: 4),
            child: Card(
              child: ListTile(
                onTap: () {
                  updateTijd(locaties[index]);
                },
                title: Text(locaties[index].locatie),
                leading: CircleAvatar(
                  backgroundImage: AssetImage('assets/${locaties[index].vlag}'),
                ),
              )
            ),
          );
        },
      )
    );
  }
}
